        newPoint = new OpenLayers.Geometry.Point(polLeft, polBottom);
        pointList.push(newPoint);
        newPoint = new OpenLayers.Geometry.Point(polRight, polBottom);
        pointList.push(newPoint);
        newPoint = new OpenLayers.Geometry.Point(polRight, polTop);
