        var mapWidth = map.getWidth();
        var mapHeight = map.getHeight();
        
        ogcSearchService.scheduleWFSMapLayerUpdate(mapLayer, bbox, mapWidth, mapHeight, this.getName());
        ogcSearchService.startPollers();
