        /* This will remove sessionId, if such exits */
        if(locationPath.match(";")) {
            locationPath = locationPath.substring(0, locationPath.indexOf(";"));
        }

        var baseUrl = window.location.protocol + "//" + window.location.host + locationPath;
        return baseUrl;
