            mapDiv.height(jQuery(window).height());
            contentMap.height(jQuery(window).height());

            if (contentMap.find('.oskariui-menutoolbar').length > 0) {
              mapDiv.height(jQuery(window).height() - contentMap.find('.oskariui-menutoolbar').height());
            }
